INSERT INTO `categorie` (`id_categorie`, `nom_cat`, `texte_cat`, `niveau`, `id_def_cat`, `id_cat_prec`) VALUES
(1, 'Matériels de suppléance visuelle', NULL, 1, 1, NULL),
(2, 'Loupes à « porter » et à « poser »', 'Les loupes à « porter » sont tenues par l’utilisateur avec la main. Les loupes à « poser »\npermettent ainsi d’éviter les problèmes de distance entre le texte et la loupe et les problèmes\nde tremblements de la main.', 2, 1, 1),
(3, 'Loupes éclairantes', 'Certaines loupes peuvent être équipées d’un éclairage. Le texte est illuminé par des ampoules\nhalogènes ou des diodes qui augmentent les capacités de lecture en améliorant les contrastes.', 2, 1, 1),
(4, 'Lunettes loupes', 'Ce sont des systèmes utilisant des verres grossissants, comme ceux des loupes, mais fixés\nsur des montures de lunettes. On peut intégrer, sur ces verres, la correction optique individuelle\nnécessaire et améliorer ainsi les capacités visuelles.', 2, 1, 1),
(5, 'Lunettes loupes éclairantes', 'Ce sont des lunettes loupes, comme celles présentées précédemment, mais elles possèdent en\nplus un éclairage, pour pouvoir améliorer les conditions de lecture, mais aussi la lecture elle-même.', 2, 1, 1),
(6, 'Monoculaires', 'Se déplacer implique d’éviter des dangers potentiels et de capter différentes informations afin\nd’atteindre la destination désirée. La lecture à distance est souvent nécessaire pour lire un nom de\nrue ou le numéro d’un bus, par exemple. Pour ce faire, des jumelles adaptées sont une solution.', 2, 1, 1),
(7, 'Loupes électroniques', 'La loupe électronique conjugue les avantages d’une loupe optique avec ceux d’un appareil\nde lecture. Il est possible d’agrandir le texte de plusieurs paliers. La luminosité ou les contrastes\npeuvent être modifiés ou inversés (lettres blanches sur fond noir, par exemple). Elles sont de petite\ntaille, très maniables, facilement transportables et se présentent comme une loupe optique.', 2, 1, 1),
(8, 'Appareils de lecture', NULL, 1, 1, NULL),
(9, 'Scanners de documents écrits', 'Ces dispositifs permettent de numériser un texte afin de l’agrandir, d’en modifier le contraste,\nvoire même de l’éditer s’il est équipé d’un logiciel de reconnaissance des caractères. Ainsi, la\npersonne déficiente visuelle pourra lire n’importe quel texte, du fait qu’elle peut l’adapter à sa vue\net ses besoins.', 2, 1, 8),
(10, 'Machines à lire', 'Ce dispositif lit des enregistrements afin de rendre accessible des livres, des magazines, des\njournaux, notamment ceux au format DAISY, pour les personnes qui ne maîtrisent pas le braille par\nexemple. Des options permettent également de modifier la tonalité ou la vitesse de lecture.', 2, 1, 8),
(11, 'Matériels braille', NULL, 1, 1, NULL),
(12, 'Machines de Perkins', 'La machine Perkins est une machine permettant d’écrire du braille sur une feuille de papier\nspécial, dont l’épaisseur est supérieure à celle du papier normal, ceci afin de pouvoir obtenir un bon\nembossage qui facilitera la lecture et diminuera la dégradation du document par l’utilisation.', 2, 1, 11),
(13, 'Plages braille', 'Les plages braille s’utilisent sur un ordinateur usuel. Via un port USB, ou autre, on le connecte\net on peut avoir accès à du contenu numérique, après transcription de l’information.', 2, 1, 11),
(14, 'Blocs-notes braille', 'Les blocs-notes braille constituent des équivalents d’ordinateurs portables pour personnes\nnon-voyantes. Ils sont équipés de leur propre environnement et s’utilisent donc indépendamment\nde tout autre dispositif numérique.', 2, 1, 11),
(15, 'Logiciels', NULL, 1, 1, NULL),
(16, 'Logiciels de téléagrandissement', 'Les logiciels d’agrandissement à installer sur un ordinateur permettent de faire apparaître à\nl’écran, des caractères agrandis, selon le besoin de grossissement.', 2, 1, 15),
(17, 'Logiciels de synthèse vocale', 'Ces logiciels permettent, à partir d’un texte entré manuellement ou d’un document écrit (type\nWord), de le lire, voire de l’enregistrer ou format audio.', 2, 1, 15),
(18, 'Logiciels de revue d’écran', 'Ces logiciels permettent à des personnes atteintes de déficiences visuelles, d’utiliser plus\nfacilement des ordinateurs, grâce à la lecture de ce qu’il y a affiché à l’écran (boîte de dialogue,\ndocument courant, fenêtre courante, etc.).', 2, 1, 15),
(19, 'Logiciels de repérage de souris', NULL, 2, 1, 15),
(20, 'Logiciels de reconnaissance des caractères', NULL, 2, 1, 15),
(21, 'Téléphones adaptés', NULL, 1, 1, NULL),
(22, 'Téléphones fixes filaires', 'Des téléphones peuvent être conçus pour des personnes qui souffrent de déficience visuelle,\nc’est-à-dire qu’ils sont adaptés aux personnes par la présence de touches facilement lisibles et\nidentifiables.', 2, 1, 21),
(23, 'Téléphones fixes sans fil', NULL, 2, 1, 21),
(24, 'Téléphones portables', NULL, 2, 1, 21),
(25, 'Applications', 'Certains applications permettent de créer une interface qui est plus adaptée pour les gens ayant\ndes déficiences visuelles.', 1, 1, NULL),
(26, 'Applications de lecture d’écran', NULL, 2, 1, 25),
(27, 'Réseau d’entraide', NULL, 2, 1, 25),
(28, 'Applications de simplification des écrans d’accueil', NULL, 2, 1, 25),
(29, 'Applications pour utiliser le braille', NULL, 2, 1, 25),
(30, 'Applications pour accéder aux livres numériques', NULL, 2, 1, 25),
(31, 'Applications de diagnostic et de stimulation visuelle', NULL, 2, 1, 25),
(32, 'Applications de zoom', NULL, 2, 1, 25),
(33, 'Applications de lecture des SMS', NULL, 2, 1, 25),
(34, 'Matériels d’aide au quotidien', NULL, 1, 1, NULL),
(35, 'Localisateurs d’objets', NULL, 2, 1, 34),
(36, 'Plans interactifs', 'Ces plans permettent aux personnes déficientes visuelles de s’orienter très facilement et de\nplanifier leur itinéraire.', 2, 1, 34),
(37, 'Réveils parlants', NULL, 2, 1, 34),
(38, 'Montres parlantes', NULL, 2, 1, 34),
(39, 'Détecteurs de couleurs et lumière', NULL, 2, 1, 34),
(40, 'Objets parlants', NULL, 2, 1, 34),
(41, 'Claviers gros caractères', NULL, 2, 1, 34),
(42, 'Matériels d’aide à l’écoute', NULL, 1, 2, NULL),
(43, 'Prothèses auditives', 'Un appareil auditif est « un appareil électronique correcteur de surdité ». Une prothèse auditive\nest conseillée après un entretien avec un audioprothésiste et un test effectué dans une cabine\ninsonorisée selon des normes spécifiques. La prothèse auditive doit répondre à plusieurs exigences\npour être qualifiée comme telle. À savoir : la possibilité de régler le gain sur au moins deux bandes\nde fréquence, et la possibilité d’ajuster cette fréquence et d’accéder à deux programmes d’écoute.\nGénéralement, il existe des applications associées à l’aide auditive pour pouvoir la contrôler depuis\nson Smartphone (ces applications sont associées à la marque du dispositif).', 2, 2, 42),
(44, 'Prothèses auditives intra-auriculaires', 'Placées à l’intérieur du conduit auditif, à des sections plus ou moins profondes du conduit,\nces aides exploitent les capacités naturelles du pavillon de l’oreille, localisent la source du son et\ndirigent celui-ci afin d’assurer une écoute claire. Dans une coque réalisée 100 % sur-mesure, ils\nsont souvent plus discrets que les contours d’oreille classiques. L’intra-auriculaire constitue ainsi\nune solution optimale pour concilier correction du déficit et exigences esthétiques.', 3, 2, 43),
(45, 'Prothèses auditives contour d’oreille', 'Un boîtier placé derrière le pavillon de l’oreille réunit les composants électroniques et transmet\nles sons au tympan par le biais d’un tube, à l’extrémité duquel se trouve un composant auriculaire\n(conçu sur-mesure ou non) positionné dans le conduit auditif. La gamme de ces appareils est très\ncomplète et permet de corriger tous les types de déficits, des plus légers aux plus sévères.', 3, 2, 43),
(46, 'Prothèses auditives mini-contour à écouteur déporté', 'Évolution technologique récente, les R.I.T.E. (littéralement « receiver in the ear ») placent\nl’écouteur directement dans le conduit auditif, tandis que le microphone et l’amplificateur prennent\nplace dans un boîtier miniaturisé qui se positionne discrètement derrière l’oreille. Faciles à mettre\nen place, confortables à porter, la qualité sonore est également naturelle. Les R.I.C. (« receiver in\ncanal »). sont similaires aux R.I.T.E.', 3, 2, 43),
(47, 'Amplificateurs de son', NULL, 2, 2, 42),
(48, 'Amplificateurs auditifs', 'Contrairement aux prothèses auditives, les amplificateurs auditifs ne sont pas prescrits par un\nmédecin O.R.L. et ne sont pas obligatoirement vendus par un audioprothésiste. Ils ne sont pas\nadaptés au niveau de surdité du patient et ne sont pas là pour corriger, mais bien pour amplifier un\nvolume sonore.', 3, 2, 47),
(49, 'Amplificateurs auditifs intra-auriculaires', NULL, 4, 2, 48),
(50, 'Amplificateurs auditifs mini-contour à écouteur déporté', NULL, 4, 2, 48),
(51, 'Amplificateurs périphériques', NULL, 3, 2, 47),
(52, 'Amplificateurs pour téléphone', NULL, 4, 2, 51),
(53, 'Amplificateurs de sonnerie téléphonique', NULL, 4, 2, 51),
(54, 'Assistants d’écoute', NULL, 4, 2, 51),
(55, 'Boucles à inductions magnétiques', 'Une boucle magnétique (amplifiée) permet à un malentendant de capter directement dans son\naide auditive, si elle est équipée de la position T, les sons amplifiés par cette boucle. Les boucles\npeuvent être installées à la maison, directement sur la télévision, par exemple, ou dans les endroits\npublics (guichets, bornes d’accueil...).', 4, 2, 51),
(56, 'Casques audio', 'Pour apprécier, à nouveau, de regarder la télévision, mais aussi un DVD, d’écouter en toute\r\nsérénité la radio ou de la musique, prendre un casque audio sans fil est la bonne solution. Les\r\ncasques audoo proposent une bonne qualité sonore et d’écoute. Ils se rechargent sur une base. La\r\ntechnologie sans fil des casques audio, spécialement conçus pour malentendants, fonctionne par\r\nradio FM ou bien par infrarouge.\r\nLes casques sont réglables, en général, en volume, en balance et pour les aigus, et s’adaptent à\r\nla perte auditive de chaque oreille. Ils sont légers, sans fil, permettant de vaquer tranquillement à\r\nses occupations et de se déplacer sans être dérangé par un fil.', 2, 2, 42),
(57, 'Matériels d’aide au quotidien', NULL, 1, 2, NULL),
(58, 'Réveils vibrants', NULL, 2, 2, 57),
(59, 'Montres vibrantes', NULL, 2, 2, 57),
(60, 'Signaux d’alerte', NULL, 2, 2, 57),
(61, 'Indicateurs de sonnerie téléphonique', NULL, 3, 2, 60),
(62, 'Indicateurs de bruit vibrants et/ou lumineux', NULL, 3, 2, 60),
(63, 'Indicateurs du volume sonore', NULL, 3, 2, 60),
(64, 'Téléphones adaptés', NULL, 1, 2, NULL),
(65, 'Téléphones portables', 'Les téléphones portables amplifiés permettent de mieux communiquer en augmentant le volume\ndu haut parleur du combiné téléphonique. De plus, les téléphones portables pour malentendants\npossèdent tous une sonnerie puissante et une position vibreur.', 2, 2, 64),
(66, 'Téléphones fixes filaires', NULL, 2, 2, 64),
(67, 'Téléphones fixes sans fil', NULL, 2, 2, 64),
(68, 'Logiciels, sites et applications', NULL, 1, 2, NULL),
(69, 'Logiciels pour ordinateur et sites', NULL, 2, 2, 68),
(70, 'Logiciels de sous-titrage', NULL, 3, 2, 69),
(71, 'Logiciels de retranscription', NULL, 3, 2, 69),
(72, 'Logiciels d’amplificateur son', NULL, 3, 2, 69),
(73, 'Logiciels de synthèse de voix', NULL, 3, 2, 69),
(74, 'Sites pour l’apprentissage de la langue des signes', NULL, 3, 2, 69),
(75, 'Applications pour Smartphone et tablette', NULL, 2, 2, 68),
(76, 'Applications d’alerte de bruit', NULL, 3, 2, 75),
(77, 'Applications de retranscription', NULL, 3, 2, 75),
(78, 'Applications de synthèse de voix', NULL, 3, 2, 75),
(79, 'Applications d’adaptateur de son', NULL, 3, 2, 75),
(80, 'Applications pour sous-titres', NULL, 3, 2, 75),
(81, 'Applications d’amplificateur son', NULL, 3, 2, 75),
(82, 'Applications pour l’apprentissage de la langue des signes', NULL, 3, 2, 75),
(83, 'Matériels d’aide à la préhension', NULL, 1, 3, NULL),
(84, 'Prothèses des membres supérieurs', 'Une prothèse est un dispositif implanté dans l’organisme pour suppléer un organe manquant ou\r\npour restaurer une fonction compromise.\r\nCette partie recense toutes les prothèses des membres supérieurs, sachant qu’un membre\r\nsupérieur est constitué d’une épaule, un bras, un coude, un avant-bras, un poignet et une main\r\npourvue de cinq doigts.\r\nUne prothèse de bras comprend : une emboîture, une articulation du coude, une articulation du\r\npoignet et une main prothétique (ou un dispositif prothétique spécifique).\r\nUne prothèse d’avant-bras comprend : une emboîture, une articulation du poignet et une main\r\nprothétique (ou un dispositif prothétique spécifique).\r\nIl existe deux types de prothèses : les exoprothèses qui se portent sur le corps et peuvent\r\ns’enlever, et les endoprothèses qui sont implantées dans le corps suite une intervention chirurgicale.\r\nNous présenterons ici uniquement les exoprothèses.', 2, 3, 83),
(85, 'Prothèses de doigts myoélectriques et mécaniques', NULL, 3, 3, 84),
(86, 'Prothèses de main myoélectriques', 'Les prothèses de main myoélectriques fonctionnent par détection des contractions musculaires\navant le moignon. Elles se différencient des prothèses esthétiques qui ne permettent pas la motricité\ndes doigts et ne font qu’apporter de la discrétion, des prothèses mécaniques qui fonctionnent avec\ndes câbles et permettent des efforts importants, mais aussi des prothèses hydrauliques qui sont\nencore plus coûteuses et peu utilisées.', 3, 3, 84),
(87, 'Prothèses de main mécaniques ou à friction', NULL, 3, 3, 84),
(88, 'Prothèses de coude myoélectriques', 'Les coudes myoélectriques se lèvent ou se baissent à l’aide de différentes commandes telles\nque les commandes myoélectriques, les interrupteurs, les touches à effleurement et les résistances\nsensibles aux pressions. Certaines de ces prothèses forment une prothèse de bras complète et\nd’autres ne permettent que l’articulation du coude.', 3, 3, 84),
(89, 'Prothèses de coude mécaniques ou à friction', 'Les coudes mécaniques ou à friction sont des choix populaires. Ils sont fonctionnels et plus\nlégers que les coudes myoélectriques. Le poids est un facteur important dont il faut tenir compte,\nparticulièrement dans le cas d’une amputation au-dessus du coude ou plus haut. Plus l’amputation\nest haute, plus il est difficile de porter une prothèse constituée de composants lourds. Les coudes\nà friction ajustables ou mécaniques (opérés par câbles) sont les modèles les plus fréquemment\nutilisés.\nLes coudes à friction ajustables permettent à la personne amputée d’utiliser sa main saine pour\nlever ou descendre l’avant-bras à la position désirée.\nLes coudes mécaniques sont commandés par un système de câbles et de harnais. Pour plier le\ncoude, la personne doit mouvoir son corps de façon à faire lever la partie inférieure du membre\nartificiel, puis le mécanisme de blocage du coude se verrouille en position pliée. Pour lever l’avantbras plus haut, l’abaisser ou le laisser pendre, la personne déverrouille le coude en tirant le câble\ngrâce au mouvement des muscles de son dos et de ses épaules.', 3, 3, 84),
(90, 'Pinces de préhension', 'Les pinces de préhension permettent de ramasser ou attraper des objets hors de portée pour les\npersonnes souffrant de déficiences motrices au niveau des membres supérieurs. Ainsi, on peut avoir\naccès à des objets en hauteur ou tombés par terre.', 2, 3, 83),
(91, 'Matériels d’aide à la lecture et l’écriture', NULL, 1, 3, NULL),
(92, 'Licornes', 'Les licornes pour personnes handicapées n’ayant pas l’usage de leurs bras permettent l’accès\naux claviers d’ordinateurs et à toutes sortes d’appareils ou aides techniques (téléphones, contacteurs\ndivers...). Elles peuvent également être utiles pour tourner les pages d’un livre.', 2, 3, 91),
(93, 'Embouts pour licornes', NULL, 3, 3, 92),
(94, 'Tournes pages', 'Les tournes pages permettent aux personnes ayant des difficultés motrices dans les doigts, les\nmains, ou les bras, de pouvoir aisément tourner les pages d’un livre, d’une revue, d’un magazine ou\nautre.', 2, 3, 91),
(95, 'Supports pour stylos et crayons de type manchon', 'Les supports pour stylos et crayons de type manchon permettent une meilleure prise en main\npour les personnes ayant des troubles moteurs dans les mains et/ou les doigts. Si certains ne font\nqu’épaissir la zone de prise et sont de forme simple, d’autres permettent de guider les doigts et sont\nplus complexes. Il existe également diverses tailles pour des stylos et crayons de diamètre différent.', 2, 3, 91),
(96, 'Autres supports pour stylos et crayons', 'Cette partie recense les supports pour stylos et crayons qui ne sont pas des manchons à enfiler.', 2, 3, 91),
(97, 'Stylos adaptés', 'Il existe plusieurs types de stylos adaptés, permettant de faciliter l’écriture et le maintien\nd’un stylo pour les personnes souffrant de troubles moteurs. Certains sont lestés, d’autres ont des\nincurvations pour permettre une position adaptée et stable des doigts, et certains ont même une\nforme particulière pour permettre un effort minimal pour un confort maximal.', 2, 3, 91),
(98, 'Logiciels d’aide à la scolarisation', 'Ces logiciels permettent aux enseignants et/ou aux parents de proposer des activités ludiques et\npédagogiques adaptées aux enfants en situation de handicap moteur lourd.', 2, 3, 91),
(99, 'Téléphones adaptés', NULL, 1, 3, NULL),
(100, 'Téléphones portables', 'Ces téléphones ont la particularité d’être simplifiés et d’avoir de grosses touches. Ainsi, les\npersonnes ayant des difficultés de motricité (tremblements, mouvements parasites, etc.) peuvent\nplus facilement les utiliser.', 2, 3, 99),
(101, 'Téléphones fixes filaires', NULL, 2, 3, 99),
(102, 'Téléphones fixes sans fil', NULL, 2, 3, 99),
(103, 'Matériels, logiciels et applications d’accessibilité numérique', NULL, 1, 3, NULL),
(104, 'Contacteurs', 'Ces dispositifs permettent d’avoir accès à un ordinateur ou à divers autres périphériques. Ils\npermettent, entre autres, d’activer ou d’éteindre ces appareils électriques. Il en existe de plusieurs\ntypes qui s’utilisent de façon différente (par pression, au souffle, optique, etc.).', 2, 3, 103),
(105, 'Souris adaptées', NULL, 2, 3, 103),
(106, 'Souris virtuelles', 'Les souris virtuelles permettent aux personnes ne pouvant se servir d’une souris classique,\nd’avoir quand même accès à ses différentes fonctionnalités.', 3, 3, 105),
(107, 'Trackballs', 'Ces dispositif permettent aux personnes déficientes motrices de remplacer la souris par une\nboule à faire rouler sous la paume.', 3, 3, 105),
(108, 'Joysticks', 'Ces dispositifs permettent aux personnes déficientes motrices de remplacer la souris par une\nmanette plus facilement maniable.', 3, 3, 105),
(109, 'Claviers adaptés', NULL, 2, 3, 103),
(110, 'Claviers virtuels', 'Ces dispositifs permettent de simuler sur l’écran d’un ordinateur un clavier standard, afin qu’une\npersonne, ne disposant pas des capacités motrices requises pour utiliser un clavier à touches, puisse\nquand même écrire du texte.', 3, 3, 109),
(111, 'Guides doigts', 'Ces dispositifs facilitent l’utilisation d’un clavier d’ordinateur quel qu’il soit, par une meilleure\ndiscrimination visuelle, ainsi qu’une meilleure visée et un meilleur maintien du doigt sur une touche.\nDe plus, ils évitent les frappes parasites ou la pression simultanée sur plusieurs touches.', 3, 3, 109),
(112, 'Claviers monomanuels', 'Ces claviers permettent aux personnes n’ayant qu’une main de fonctionnelle, d’avoir accès à\ntoutes les fonctionnalités d’un clavier d’ordinateur standard avec une seule main.', 3, 3, 109),
(113, 'Claviers adaptés pour enfants', 'Ces claviers sont destinés aux enfants en bas âge et souffrant de déficiences motrices, qui\ncommencent à lire, écrire et compter, ou qui souhaitent avoir facilement accès aux jeux en ligne.', 3, 3, 109),
(114, 'Navigation sans les mains', NULL, 2, 3, 103),
(115, 'Reconnaissance vocale', 'La reconnaissance vocale permet, aux personnes ne pouvant se servir de leurs membres supé-\nrieurs, de dicter à l’ordinateur le texte à écrire, voire de naviguer sur leur ordinateur grâce à leur\nvoix.', 3, 3, 114),
(116, 'Commande oculaire', 'La commande oculaire permet aux personnes ne pouvant se servir de leurs membres supérieurs,\nde naviguer sur leur ordinateur en utilisant les mouvements de leurs yeux.', 3, 3, 114),
(117, 'Contrôle par mouvements de la tête', 'Le contrôle par mouvements de la tête permet aux personnes ne pouvant se servir de leurs\nmembres supérieurs, d’utiliser les mouvements de leur tête pour naviguer sur leur ordinateur.', 3, 3, 114),
(118, 'Supports pour ordinateurs, tablettes', 'Ces supports permettent aux personnes alitées d’avoir accès à leur ordinateur portable ou leur\ntablette numérique, tout en gardant une position la plus confortable possible.', 2, 3, 103),
(119, 'Matériels d’aide au quotidien', NULL, 1, 3, NULL),
(120, 'Détecteurs de chutes', 'Ces dispositifs permettent de détecter lorsqu’une personne souffrant de déficiences motrices a\nchuté, afin de prévenir les secours et/ou les proches. La personne n’a donc pas besoin de se déplacer\npour attraper un téléphone ou autre, pour communiquer avec le monde extérieur.', 2, 3, 119),
(121, 'Broyeurs de comprimés', 'Les broyeurs de comprimés permettent de réduire facilement des comprimés avant de faciliter\nleur prise par des personnes ayant des difficultés à déglutir.', 2, 3, 119),
(122, 'Coupeurs de comprimés', 'Les coupes comprimés permettent aux personnes, qui suivent un traitement médicamenteux, de\ncouper plus facilement leurs comprimés. Ces dispositifs sont appropriés pour les personnes ayant\npeu de force dans les membres supérieurs et/ou qui ont des difficultés à manipuler de petits objets.', 2, 3, 119),
(123, 'Coupeurs-broyeurs de comprimés', 'Ces dispositifs permettent à la fois de broyer et de couper en deux un comprimé. Ils permettent\nainsi d’éviter d’acheter séparément un broyeur de comprimés et un coupeur de comprimés.', 2, 3, 119),
(124, 'Guides gouttes', 'Un guide gouttes permet aux personnes souffrant de troubles moteurs au niveau des membres\nsupérieurs de pouvoir, néanmoins, s’administrer les médicaments à prendre par voie oculaire. Ce\ndispositif peut particulièrement aider les personnes affligées de tremblements dans les mains.', 2, 3, 119),
(125, 'Téléphones adaptés', NULL, 1, 4, NULL),
(126, 'Téléphones fixes sans fil', NULL, 2, 4, 125),
(127, 'Téléphones fixes filaires', NULL, 2, 4, 125),
(128, 'Téléphones portables', NULL, 2, 4, 125),
(129, 'Applications', NULL, 1, 4, NULL),
(130, 'Applications d’apprentissage', NULL, 2, 4, 129),
(131, 'Applications pour les enfants dyslexiques', NULL, 2, 4, 129),
(132, 'Applications pour la lecture', NULL, 2, 4, 129),
(133, 'Applications de communication par pictogrammes', 'Pour des personnes ayant des difficultés à s’exprimer de manière traditionnelle,il existe une\ncommunication spéciale dite C.A.A. en français. La communication améliorée et alternative C.A.A.)\nest un terme générique qui englobe les méthodes de communication utilisées pour compléter ou\nremplacer la parole ou l’écriture pour les personnes ayant une déficience dans la production ou la\ncompréhension du langage.', 2, 4, 129),
(134, 'Logiciels', 'La démocratisation de l’ordinateur et des nouvelles technologies ont permis la création de\ndivers logiciels pour aider les personnes en situation de handicap mental. Les logiciels peuvent\nservir à des exercices avec des psychologues ou des pédiatres, pour des enfants atteints du trouble\nspectral de l’autisme par exemple. Des logiciels existent aussi pour faire des dépistages, comme\npour la maladie d’Alzheimer ou l’autisme sur de jeunes enfants. Ils permettent aussi aux enfants\net à toutes les personnes en situation de handicap de refaire des exercices en dehors des exercices\navec les psychologues pour stimuler leur cerveau et améliorer leur capacité.', 1, 4, NULL),
(135, 'Logiciels d’apprentissage', NULL, 2, 4, 134),
(136, 'Logiciels de stimulation mémorielle', NULL, 2, 4, 134),
(137, 'Logiciels de reconnaissance des caractères', NULL, 2, 4, 134),
(138, 'Logiciels de prédiction des mots', NULL, 2, 4, 134),
(139, 'Logiciels de bureautique', 'La bureautique est l’ensemble des techniques et des moyens tendant à automatiser les activités\nde bureau et, principalement, le traitement et la communication de la parole, de l’écrit et de l’image.\nLes logiciels de bureautique regroupent des traitements de textes, des tableurs, des logiciels de\nPrésentation Assistée par Ordinateur (PréAO) ou des logiciels de comptabilité et de gestion de projet.', 2, 4, 134),
(140, 'Logiciels de communication par pictogrammes', NULL, 2, 4, 134),
(141, 'Matériels informatiques périphériques', NULL, 1, 4, NULL),
(142, 'Claviers', NULL, 2, 4, 141),
(143, 'Trackballs', 'Une trackball, ou boule de commande, est un périphérique informatique permettant de déplacer\ndes objets virtuels sur un écran d’ordinateur au travers de mouvements exercés sur une sphère\nintégrée au périphérique. L’utilisateur fait « rouler » la partie émergée de la boule (avec son pouce,\nson index ou la paume de sa main en fonction des modèles). Différents boutons, placés à des\nendroits accessibles par le bout des doigts, permettent de sélectionner des actions.', 2, 4, 141),
(144, 'Guides doigts', NULL, 2, 4, 141),
(145, 'Matériels d’aide au quotidien', NULL, 1, 4, NULL),
(146, 'Piluliers', 'La prise de médicament pour les personnes en situation de handicap est un moment délicat car\nil ne faut pas les oublier ni les prendre plusieurs fois. Les dispositifs suivants permettent de palier\nles oublis et/ou les surdosages qui peuvent être nocifs voire mortels pour certains personnes.', 2, 4, 145),
(147, 'Orientation dans le temps', NULL, 2, 4, 145),
(148, 'Calendriers', NULL, 3, 4, 147),
(149, 'Horloges', NULL, 3, 4, 147),
(150, 'Montres', NULL, 3, 4, 147),
(151, 'Réveils', 'Les troubles du sommeil chez l’enfant en situation de handicap sont très fréquents, et leurs\norigines diverses. Ils sont souvent générateurs de difficultés au sein de la famille, pour l’enfant\nlui-même, bien sûr, mais aussi pour l’ensemble de la famille.', 3, 4, 147),
(152, 'Communication par pictogrammes', NULL, 2, 4, 145),
(153, 'Soutien psychologique', NULL, 2, 4, 145),
(154, 'Signaux d’alerte', NULL, 2, 4, 145),
(155, 'Appareils pour la mémoire', NULL, 3, 4, 154),
(156, 'Appareils de rappel', NULL, 4, 4, 155),
(157, 'Localisation d’objet', NULL, 4, 4, 155),
(158, 'Signaux d’ouverture', NULL, 3, 4, 154),
(159, 'Systèmes de repérage de personnes', NULL, 3, 4, 154),
(160, 'Télé-alarmes', NULL, 3, 4, 154),
(161, 'Matériels d’aide à la parole', NULL, 1, 5, NULL),
(162, 'Amplificateurs de voix', NULL, 2, 5, 161),
(163, 'Appareils de communication par synthèse vocale', NULL, 2, 5, 161),
(164, 'Supports de communication par pictogrammes', NULL, 2, 5, 161),
(165, 'Lecteurs et enregistreurs audio et/ou vidéo', NULL, 2, 5, 161),
(166, 'Logiciels', NULL, 1, 5, NULL),
(167, 'Logiciels d’apprentissage de la langue des signes', NULL, 2, 5, 166),
(168, 'Logiciels de communication par pictogrammes', NULL, 2, 5, 166),
(169, 'Logiciels de communication par synthèse vocale', NULL, 2, 5, 166),
